mapper.select:
select pk_empnum
from tbl_employee
where pk_empnum = #{pk_empnum} and  email = #{email}

mapper.update:
update tbl_employee set postcode = #{mypostcode} , address = #{myaddress} , detailaddress = #{mydetailAddress} 

, extraaddress = #{myextraAddress}

where pk_empnum = #{pk_empnum}

mapper.select:
select pk_board_seq, fk_empnum, name, b_subject, b_content, b_readcount, b_regdate, b_pw
, groupno, fk_seq, depthno , b_filename, b_orgfilename, b_filesize from ( select B.pk_board_seq, B.fk_empnum, E.name, B.b_subject, B.b_content, B.b_readcount ,to_char(B.b_regdate, 'yyyy-mm-dd hh24:mi:ss') as b_regdate , B.b_pw
, B.groupno, B.fk_seq, B.depthno , B.b_filename, B.b_orgfilename, B.b_filesize
from tbl_board B JOIN tbl_employee E ON B.FK_EMPNUM = E.PK_EMPNUM where b_status = 1 order by B.PK_BOARD_SEQ desc
) V where V.PK_BOARD_SEQ = #{pk_board_seq}

mapper.update:
    update tbl_board set b_subject = #{b_subject}
	                   , b_content = #{b_content}
	where pk_board_seq = #{pk_board_seq}

    	update tbl_board  set  b_subject = #{b_subject} , b_content = #{b_content}
    						, b_filename = #{newFileName} , b_orgfilename = #{originalFilename} , b_filesize = #{fileSize}
		where pk_board_seq = #{pk_board_seq}

mapper.select:
select pk_board_seq, fk_empnum, name, b_subject, b_content, b_readcount, b_regdate, b_pw
, groupno, fk_seq, depthno , b_filename, b_orgfilename, b_filesize , emppicturename, deptnamekor ,spotnamekor from ( select B.pk_board_seq, B.fk_empnum, E.name, B.b_subject, B.b_content, B.b_readcount ,to_char(B.b_regdate, 'yyyy-mm-dd hh24:mi:ss') as b_regdate , B.b_pw
, B.groupno, B.fk_seq, B.depthno , B.b_filename, B.b_orgfilename, B.b_filesize ,E.emppicturename , D.deptnamekor, S.spotnamekor
from tbl_board B JOIN tbl_employee E ON B.FK_EMPNUM = E.PK_EMPNUM JOIN TBL_SPOT S ON E.fk_spotnum = S.pk_spotnum JOIN TBL_DEPARTMENT D ON E.fk_deptnum = D.pk_deptnum where b_status = 1 order by B.PK_BOARD_SEQ desc
and lower(${searchType}) like '%'|| lower(#{searchWord}) ||'%'
) V where V.pk_board_seq = #{pk_board_seq}

 
		select ceil(count(*)/#{sizePerPage})
		from tbl_board_comment
		where fk_board_seq = #{fk_board_seq}
