mapper.select:
select pk_empnum
from tbl_employee
where pk_empnum = #{pk_empnum} and  email = #{email}

mapper.update:
update tbl_employee set postcode = #{mypostcode} , address = #{myaddress} , detailaddress = #{mydetailAddress} 

, extraaddress = #{myextraAddress}

where pk_empnum = #{pk_empnum}

mapper.select:
select PK_BOARD_SEQ, FK_EMPNUM, name, B_SUBJECT, B_CONTENT, B_READCOUNT, B_REGDATE, B_PW
, groupno, fk_seq, depthno , B_FILENAME, B_ORGFILENAME, B_FILESIZE from ( select B.PK_BOARD_SEQ, B.FK_EMPNUM, E.name, B.B_SUBJECT, B.B_CONTENT, B.B_READCOUNT ,to_char(B.B_REGDATE, 'yyyy-mm-dd hh24:mi:ss') as B_REGDATE , B.B_PW
, B.groupno, B.fk_seq, B.depthno , B.B_FILENAME, B.B_ORGFILENAME, B.B_FILESIZE
from tbl_board B JOIN tbl_employee E ON B.FK_EMPNUM = E.PK_EMPNUM where b_status = 1 order by B.PK_BOARD_SEQ desc
) V where V.PK_BOARD_SEQ = #{pk_board_seq}
