mapper.select:
select pk_empnum, name, address, detailaddress, postcode, phone, email, birthday, gender, registerday, startday, resignationstatus, resignationday, fk_vstatus, emppicturename, salary, lastpwdchangedate, emppicturefilename, spotnamekor, deptnamekor from ( select row_number() over(order by pk_empnum desc) AS rno, pk_empnum, name, address, detailaddress, postcode, phone, email, birthday, gender, registerday, startday, resignationstatus, resignationday, fk_vstatus, emppicturename, salary, lastpwdchangedate, emppicturefilename, spotnamekor, deptnamekor from ( SELECT E.pk_empnum, E.name, E.address, E.detailaddress, E.postcode, E.phone, E.email, E.birthday, E.gender, E.registerday, to_char(E.startday, 'yyyy-mm-dd') as startday, E.resignationstatus, E.resignationday, E.fk_vstatus, E.emppicturename, E.salary, E.lastpwdchangedate, E.emppicturefilename, s.spotnamekor, s.pk_spotnum, d.deptnamekor, d.pk_deptnum from tbl_employee E JOIN TBL_SPOT S ON E.fk_spotnum = S.pk_spotnum JOIN TBL_DEPARTMENT D ON E.fk_deptnum = D.pk_deptnum ) V where 1=1
and pk_deptnum = #{dept}
and pk_spotnum = #{spot}
and startday between #{date_start} and #{date_end}
and resignationstatus = #{resign_status}
and ${search_item} like '%'||#{search_value}||'%'
and ${search_item} = #{search_value}
order by pk_empnum desc
order by name asc
order by pk_spotnum desc
order by startday desc
) V2 where rno between #{startRno} and #{endRno}

select count(*) from ( SELECT E.pk_empnum, E.name, E.address, E.detailaddress, E.postcode, E.phone, E.email, E.birthday, E.gender, E.registerday, to_char(E.startday, 'yyyy-mm-dd') as startday, E.resignationstatus, E.resignationday, E.fk_vstatus, E.emppicturename, E.salary, E.lastpwdchangedate, E.emppicturefilename, s.spotnamekor, s.pk_spotnum, d.deptnamekor, d.pk_deptnum from tbl_employee E JOIN TBL_SPOT S ON E.fk_spotnum = S.pk_spotnum JOIN TBL_DEPARTMENT D ON E.fk_deptnum = D.pk_deptnum ) V where 1=1
and pk_deptnum = #{dept}
and pk_spotnum = #{spot}
and startday between #{date_start} and #{date_end}
and resignationstatus = #{resign_status}
and ${search_item} like '%'||#{search_value}||'%'
and ${search_item} = #{search_value}

SELECT pk_empnum, name, deptnamekor, resignationstatus, total_late, total_early_endcheck , total_no_endcheck, total_no_workday, totalworkingdays, totalworkedtime FROM ( SELECT ROWNUM AS rno , pk_empnum, name, deptnamekor, resignationstatus, total_late, total_early_endcheck , total_no_endcheck, total_no_workday, totalworkingdays, totalworkedtime from ( SELECT pk_empnum, name, deptnamekor, resignationstatus, total_late, total_early_endcheck , total_no_endcheck, total_no_workday, totalworkingdays , TOTALWORKED_DAY||'일'|| ASTOTALWORKED_HH||'시'|| TOTALWORKED_MI||'분'|| TOTALWORKED_SS||'초' as totalworkedtime FROM ( select name, pk_empnum, fk_deptnum , case when RESIGNATIONSTATUS in(0) then '재직' else '퇴직' end AS RESIGNATIONSTATUS from tbl_employee where 1=1
and name like '%'||#{search_value}||'%'
and pk_empnum = #{search_value}
and fk_deptnum = #{dept}
) E JOIN ( select fk_empnum , sum(LATE) as total_late , sum(EARLY_ENDCHECK) as total_EARLY_ENDCHECK , sum(NO_ENDCHECK) as total_NO_ENDCHECK , sum(NO_WORKDAY) as total_NO_WORKDAY from tbl_commute_status group by fk_empnum ) S ON E.pk_empnum = S.fk_empnum JOIN ( select PK_DEPTNUM, DEPTNAMEKOR from TBL_DEPARTMENT ) D on D.PK_DEPTNUM = E.fk_deptnum JOIN ( select fk_empnum, count(*) as totalworkingdays from TBL_COMMUTE
where to_char(startwork, 'yyyy-mm-dd') between to_date(#{date_start}, 'yyyy-mm-dd') and to_date(#{date_end}, 'yyyy-mm-dd')
group by fk_empnum ) C on c.fk_empnum = E.pk_empnum JOIN ( select FK_EMPNUM, TOTALWORKED_DAY, ASTOTALWORKED_HH, TOTALWORKED_MI, TOTALWORKED_SS from totalworkedtimecalc ) W on c.fk_empnum = W.FK_EMPNUM
order by total_late desc
order by total_early_endcheck desc
order by total_no_endcheck desc
order by total_no_workday desc
order by totalworkingdays desc
order by totalworkedtime desc
) ) where rno between #{startRno} and #{endRno}

select fk_empnum, startwork, endwork, today, TODAYWORKEDTIME , LATE, EARLY_ENDCHECK, NO_ENDCHECK, NO_WORKDAY, totalworkedtime from ( select row_number() over(order by today desc) AS rno , c.fk_empnum , to_char(startwork, 'hh24:mi:ss') as startwork , to_char(endwork, 'hh24:mi:ss') as endwork , to_char(TODAY, 'yyyy-mm-dd') as today, TODAYWORKEDTIME, LATE, EARLY_ENDCHECK, NO_ENDCHECK, NO_WORKDAY , TOTALWORKED_DAY||'일'|| ASTOTALWORKED_HH||'시'|| TOTALWORKED_MI||'분'|| TOTALWORKED_SS||'초' as totalworkedtime from tbl_commute C join tbl_commute_status S on c.fk_empnum = s.fk_empnum left JOIN totalworkedtimecalc T on c.fk_empnum = T.FK_EMPNUM where C.fk_empnum = #{pk_empnum} and to_char(startwork, 'yyyy-mm-dd') = to_char(today, 'yyyy-mm-dd')
and to_char(today, 'yyyy-mm-dd') between #{date_start} and #{date_end}
) where 1=1
and rno between #{startRno} and #{endRno}

select count(*) from (
select count(*) from ( select S.fk_empnum , name, pk_empnum, fk_deptnum , to_char(startwork, 'yyyy-mm-dd'), to_char(endwork, 'yyyy-mm-dd') from tbl_commute_status S JOIN TBL_employee E on S.fk_empnum = E.pk_empnum JOIN TBL_commute C on S.fk_empnum = C.fk_empnum where 1=1
and fk_deptnum = #{dept}
and to_char(startwork, 'yyyy-mm-dd') between to_date(#{date_start}, 'yyyy-mm-dd') and to_date(#{date_end}, 'yyyy-mm-dd')
and ${search_item} like '%'||#{search_value}||'%'
and ${search_item} = #{search_value}
) group by fk_empnum )

	select pk_empnum
    from tbl_employee
    where pk_empnum not in ( 
    select fk_empnum
    from tbl_commute where to_char(startwork, 'yyyy-mm-dd') = to_char(sysdate, 'yyyy-mm-dd')
    ) and pk_empnum not in ( 
    select fk_empnum 
    from TBL_VACATIONLIST where to_char(sysdate, 'yyyy-mm-dd') between to_date(substr(vstartdate, 0, 10), 'yyyy-mm-dd') and to_date(substr(venddate, 0, 10), 'yyyy-mm-dd')
	)

select to_char(startwork, 'hh24:mi:ss') as startwork, to_char(endwork, 'hh24:mi:ss') as endwork
from tbl_commute
where to_char(startwork, 'yyyy-mm-dd') = to_char(sysdate, 'yyyy-mm-dd') and fk_empnum = #{login_empnum}

select count(*)
from tbl_commute
where fk_empnum = #{pk_empnum}

and to_char(startwork, 'yyyy-mm-dd') between #{date_start} and #{date_end}
